import { Avatar, Badge, Box, Button, Divider, Flex, Heading, Input, List, ListItem, Text } from '@chakra-ui/react';
import React, { useState, useCallback } from 'react';

import { api } from '../../services/api';

const Home = () => {

    const IUser = {
        avatar_url: '',
        bio: '',
        name: ''
    };

    const [userName, setUserName] = useState('');
    const [infoUser, setInfoUser] = useState({});
    const [repo, setRepo] = useState(null);


    const handleSearch = useCallback(async () => {
        if (userName) {
            const { data: { avatar_url, bio, name } } = await api.get(`users/${userName}`);
            setInfoUser({ avatar_url, bio, name });
            //console.log(data);
        }
    }, [userName]);


    return (
        <Flex flexDir="column" alignItems="center" paddingX={10} paddingY={5}>
            <Box width="480px">
                <Flex flexDir="column">
                    <Heading>Listagem de repositórios</Heading>
                    <Input
                        type="text"
                        placeholder="Username"
                        marginTop="3"
                        value={userName}
                        onChange={e => setUserName(e.target.value)}
                    />
                    <Button type="button" marginTop="6" onClick={handleSearch}>Pesquisar</Button>
                </Flex>
                <Flex flexDir="column" marginTop={10}>
                    <List>
                        <ListItem marginBottom={4}>
                            <Box bg="gray.300" padding={5} rounded="lg">
                                <Flex alignItems="center">
                                    <Avatar size="lg" name="Kris"
                                        src="https://github.com/krishnamuti.png" />
                                    <Heading marginLeft={2} fontSize="2xl">Kris</Heading>
                                </Flex>
                                <Divider />
                                <Text>Descrição</Text>
                            </Box>
                        </ListItem>

                        <ListItem marginBottom={4}>
                            <Box bg="gray.300" padding={5} rounded="lg">
                                <Heading fontSize="2xl">Titulo teste</Heading>
                                <Divider />
                                <Text>Descrição</Text>
                                <Divider />
                                <Badge variant="solid">Typescript</Badge>
                            </Box>
                        </ListItem>
                    </List>
                </Flex>
            </Box>
        </Flex>
    );
};

export { Home };
